import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ExerciseList = () => {
    const [exercises, setExercises] = useState([]);
    const [muscle, setMuscle] = useState('');
    const [updated, setUpdated] = useState(false);
    const [apiKey, setApiKey] = useState('bdlU1S+eNDwchHsZpo1iCA==a5WqVnw7wK9xHQZ0');

    const getExercises = (muscle) => {
        axios
            .get(`https://api.api-ninjas.com/v1/exercises?muscle=${muscle}`, {
                headers: {
                'X-Api-Key': apiKey,
                'Content-Type': 'application/json'
                }
            })
            .then((response) => {
                setExercises(response.data);
            })
            .catch((error) => {
                console.error('Error: ', error);
            });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        getExercises(muscle);
        setUpdated(!updated);
    };

    useEffect(() => {
		getExercises(muscle);
	}, [updated]);

    return (
        <div>
        <form onSubmit={handleSubmit}>
            <label>
            Muscle:
            <input type="text" value={muscle} onChange={(event) => setMuscle(event.target.value)} />
            </label>
            <button type="submit">Search</button>
        </form>
        <h1>Exercises for {muscle}:</h1>
        <ul>
            {exercises.map(exercise => (
            <li key={exercise.id}>{exercise.name}</li>
            ))}
        </ul>
        </div>
    );
};

export default ExerciseList;
