import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ExerciseList = () => {
    const [exercises, setExercises] = useState([]);
    const [muscle, setMuscle] = useState('');
    const [updated, setUpdated] = useState(false);
    const [apiKey, setApiKey] = useState('bdlU1S+eNDwchHsZpo1iCA==a5WqVnw7wK9xHQZ0');
    const [showDetails, setShowDetails] = useState({});

    const getExercises = (muscle) => {
        axios
            .get(`https://api.api-ninjas.com/v1/exercises?muscle=${muscle}`, {
                headers: {
                'X-Api-Key': apiKey,
                'Content-Type': 'application/json'
                }
            })
            .then((response) => {
                setExercises(response.data);
            })
            .catch((error) => {
                console.error('Error: ', error);
            });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        getExercises(muscle);
        setUpdated(!updated);
    };

    const toggleDetails = (exerciseId) => {
        setShowDetails((prevState) => ({
            ...prevState,
            [exerciseId]: !prevState[exerciseId],
        }));
    };


    useEffect(() => {
		getExercises(muscle);
	}, [updated]);

    return (
        <div className='mt-4 mx-auto w-8/12 py-8 px-6 shadow-xl rounded-lg sm:px-10 '>
            <h1 className='mb-3 block text-xl font-medium text-gray-700 text-center'>Search for Exercises</h1>
            <form className="bg-white py-8 px-6 shadow-lg rounded-lg sm:px-10" onSubmit={handleSubmit}>
                <label className='block text-xl font-medium text-gray-700 mt-1' htmlFor="muscle">Muscle:&nbsp;
                <input type="text" value={muscle} onChange={(event) => setMuscle(event.target.value)} className="flex-1 px-3 py-2 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-600 focus:border-transparent" placeholder="Enter muscle" />
                </label>
                <br/>
                <button className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded" type="submit">Search</button>
            </form>
            <br/>
            <h1 className='block text-xl font-medium text-gray-700'>Exercises For: {muscle}</h1>
            <ul>
                {exercises.map(exercise => (
                // <li key={exercise.id} className="border border-red-500 p-4 my-4 bg-gray-100 rounded-lg p-4 mb-4">
                //     <label className='block text-xl font-medium text-gray-700' htmlFor="name">Name: {exercise.name}</label>
                //     <label className='block text-xl font-medium text-gray-700' htmlFor="type">Type: {exercise.type}</label>
                //     <label className='block text-xl font-medium text-gray-700' htmlFor="muscle">Muscle: {exercise.muscle}</label>
                //     <label className='block text-xl font-medium text-gray-700' htmlFor="equipment">Equipment: {exercise.equipment}</label>
                //     <label className='block text-xl font-medium text-gray-700' htmlFor="difficulty">Difficulty: {exercise.difficulty}</label>
                //     <label className='block text-xl font-medium text-gray-700' htmlFor="instructions">Instructions: {exercise.instructions}</label>
                //     <br/>
                // </li>
                <li key={exercise.id} className="border border-red-500 p-4 my-4 bg-gray-100 rounded-lg p-4 mb-4">
                <div className="flex justify-between items-center">
                  <div>
                    <h2 className='text-xl font-medium text-gray-700'>{exercise.name}</h2>
                    <p className='text-gray-600'>{exercise.muscle} - {exercise.difficulty}</p>
                  </div>
                  <button
                    className="text-blue-500 hover:text-blue-700 font-medium focus:outline-none"
                    onClick={() => toggleDetails(exercise.id)}
                  >
                    {showDetails[exercise.id] ? 'Hide' : 'Show'}
                  </button>
                </div>
                {showDetails[exercise.id] && (
                  <div>
                    <label className='block text-xl font-medium text-gray-700' htmlFor="type">Type: {exercise.type}</label>
                    <label className='block text-xl font-medium text-gray-700' htmlFor="equipment">Equipment: {exercise.equipment}</label>
                    <label className='block text-xl font-medium text-gray-700' htmlFor="instructions">Instructions: {exercise.instructions}</label>
                  </div>
                )}
                </li>
                ))}
            </ul>
        </div>
    );
};

export default ExerciseList;
