import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'

const HomePage = () => {

    const [workouts, setWorkouts]= useState([]);
    const [search, setSearch]= useState("")
    const [searchField, setSearchField] = useState("")
    const [checkSearch, setcheckSearch] = useState(false)

    const handleChange = (event) => {
        setSearchField(event.target.value)
    }

    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            setSearch(searchField)
            setcheckSearch(true)
        }
    }

    const getOneWorkout = (id) => {
        return axios.get(`http://localhost:3000/workouts/${id}`)
            .then((response)=>{
                return response.data
            })
            .catch((error)=> {
                console.log(error)
            })
        }

    const getWorkouts = () => {
        axios.get('https://localhost:3000/workouts')
        .then((response) => setWorkouts(response.data), 
        (err) => console.log(err))
        .catch((error) => console.log(error))
        }

        useEffect (()=> {
            getWorkouts()
        },[])

    return(
        <>
            <div className= "flex flex-col items-center justify-center w-screen h-96 bg-[url('https://hips.hearstapps.com/hmg-prod/images/best-luxury-gyms-london-1577449934.jpg')] bg-cover bg-no-repeat bg-center" >
                <p className="sm:text-[70px] text-[50px] text-#64748b my-0 mx-auto text-center">STRIVE to be GREAT</p>
                <input className="my-0 text-black border border-gray-400 rounded-md w-96 px-4 py-3 mt-4"type="text" placeholder="Search for a Workout" onChange={handleChange} onKeyDown={handleKeyDown}/>
            </div>
            <div className="mt-10">
                <h3 className="text-[25px] text-center">Workouts</h3>
                {checkSearch ? <p className="text-[15px] text-center">Search results: {search}</p> : <></>}
            </div>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 mt-5 mx-auto max-w-[1700px]">
                {workouts.filter((workout)=> {
                    if(search === ""){
                        return workout
                    } else if(workout.title.toLowerCase().includes(search.toLowerCase())) {
                        return workout
                    }
                })
                .map((workout)=>{
                    return (                
                    <div className="relative flex" key={workout.id}>
                        <Link to={`/oneWorkout/${workout.id}`} onClick={() => getOneWorkout(workout.id)}>
                            <div className="absolute inset-0 flex flex-col justify-end px-4 py-6">
                                <p className="text-white text-xl mb-2"><strong>${workout.title}</strong></p>
                                <p className="text-white">{workout.date} {workout.time} {workout.duration} {workout.difficulty}, {workout.notes}</p>
                            </div>
                        </Link>
                    </div>
                    )
                })}
                </div>
        </>
    )
}

export default HomePage
