import React, {useState, useEffect} from 'react'
import axios from 'axios'
import { Link, useParams } from 'react-router-dom'
import Create from './Create';
import Update from './Update';

const Workout = () => {
    const [workouts, setWorkouts] = useState([]);
    let [newWorkout, setNewWorkout] = useState([]);
    const [updated, setUpdated] = useState(false);

    const {id} = useParams()

    const getWorkouts = () => {
    axios.get('http://localhost:3000/workouts/')
        .then((response) => {
            console.log(response.data);
            setWorkouts(response.data)
        }, 
        (err) => console.log(err))
        .catch((error) => console.log(error))
    }

    const getOneWorkout = (id) => {
        return axios.get(`http://localhost:3000/workouts/${id}`)
            .then((response)=>{
                return response.data
            })
            .catch((error)=>{
                console.log(error);
            })
    }


    //Update
    const handleUpdate = (editWorkout) => {
    axios
        .put(`http://localhost:3000/workouts/${editWorkout.id}`, 
            editWorkout
        )
        .then((response) => {
            getWorkouts();
        })
        .catch((error)=>{
            console.log(error);
        })
    }

    useEffect(() => {
    getWorkouts();
}, [updated]);

    return(
        <>
            <div className="grid grid-cols-fluid gap-6 mt-5">
                {workouts.map((workout)=> {
                return (
                    <div>
                        <div>
                            <Link to={`/oneWorkout/${workout.id}`} onClick={() => getOneWorkout(workout.id)}>
                            </Link>
                        </div>
                    </div>
                )
                })}
            </div>
        </>
    )
}

export default Workout
